pragma solidity ^0.8.4;

import "hardhat/console.sol";
import "./RLE.sol";

contract LightRunners {
    bytes public content;

    function setContent(bytes calldata _content) public {
        content = RLE.decode(_content);
    }
}

contracts/RLE.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

library RLE {
    function decode(bytes calldata _rleBytes)
        public
        pure
        returns (bytes memory)
    {
        bytes memory decodedBytes;
        for (uint256 i = 0; i < _rleBytes.length; i += 2) {
            uint8 count = uint8(_rleBytes[i]);
            bytes1 current = _rleBytes[i + 1];
            for (uint8 j = 0; j < count; j++) {
                decodedBytes = bytes.concat(decodedBytes, current);
            }
        }
        return decodedBytes;
    }
}

contracts/Greeter.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

import "hardhat/console.sol";

contract Greeter {
    struct Greeting {
        uint8 id;
        string text;
    }

    Greeting[] public greetings;

    string private greeting;

    constructor(string memory _greeting) {
        greeting = _greeting;
    }

    function greet() public view returns (string memory) {
        return greeting;
    }

    function setGreeting(string memory _greeting) external {
        greeting = _greeting;
    }

    function setGreetings(Greeting[] calldata _greetings) external {
        for (uint16 i = 0; i < _greetings.length; i++) {
            greetings.push(_greetings[i]);
        }
    }

    function addGreeting(uint256 _id, string calldata _text) external {
        greetings.push(Greeting(uint8(_id), _text));
    }

    function addGreeting(uint256 _id) external {
        greetings.push(Greeting(uint8(_id), "test"));
    }
}
